import React, { useState, useEffect } from 'react';
import './home.css';

const API_ENDPOINT = "https://api.thenewsapi.com/v1/news/headlines";
const API_TOKEN = "bkuqJV8oiKYiRSAlsEkQGJITG882ZTJ6UgMYg97K";

function NewsFeed() {
    const [articles, setArticles] = useState([]);

    useEffect(() => {
        fetch(`${API_ENDPOINT}?language=en&api_token=${API_TOKEN}&headlines_per_category=10`)
            .then(response => response.json())
            .then(data => {
                const coffeeArticles = data.data.general.filter(article => 
                    article.title.toLowerCase().includes('coffee') ||
                    article.description.toLowerCase().includes('coffee')
                );
                setArticles(coffeeArticles);
            });
    }, []);

    return (
        <div className="news-feed">
            {articles.map(article => (
                <div className="article" key={article.uuid}>
                    <img src={article.image_url} alt={article.title} />
                    <h2>{article.title}</h2>
                    <p>{article.description}</p>
                    <a href={article.url} target="_blank" rel="noopener noreferrer">Read more</a>
                </div>
            ))}
        </div>
    );
}

export default NewsFeed;
