import React, { useEffect, useState } from 'react';
import { LineChart, Line, ResponsiveContainer } from 'recharts';

const CommoditiesPrices = () => {
    const [prices, setPrices] = useState({ COFFEE: null, ROBUSTA: null });
    const [units, setUnits] = useState({ COFFEE: null, ROBUSTA: null });
    const [coffeeChartData, setCoffeeChartData] = useState([]);
    const [robustaChartData, setRobustaChartData] = useState([]);


    const divStyle = {
        padding: '20px',
        margin: '10px',
        width: '200px',
        background: 'rgba(0,255,133,0.35)',
        boxShadow: '20px 20px 40px -6px rgba(0,0,0,0.2)',
        backdropFilter: 'blur(10.5px)',
        WebkitBackdropFilter: 'blur(10.5px)',
        borderRadius: '10px',
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        justifyContent: 'center'
    };

    useEffect(() => {
        const fetchData = async () => {
            try {
                const response = await fetch(`https://commodities-api.com/api/latest?access_key=nc4rq9ho37s9r4m7r7bd4jaw46b940amnq40h71wcj54cbfweprfm113awf4&base=USD&symbols=COFFEE,ROBUSTA`);
                const { data } = await response.json();

                console.log("API Response:", data);

                if (data && data.rates) {
                    const coffeePrice = data.rates.COFFEE;
                    const robustaPrice = data.rates.ROBUSTA;
                    setPrices({ COFFEE: coffeePrice, ROBUSTA: robustaPrice });

                    // Setting units
                    if (data.unit) {
                        setUnits({ COFFEE: data.unit.COFFEE, ROBUSTA: data.unit.ROBUSTA });
                    }
                }

            } catch (error) {
                console.error("Error fetching commodities data:", error);
            }
        };

        fetchData();


    const fetchTimeSeries = async () => {
        const currentDate = new Date();
        const endDate = currentDate.toISOString().split('T')[0];
        const startDate = new Date(currentDate.setDate(currentDate.getDate() - 5)).toISOString().split('T')[0];

        const response = await fetch(`https://commodities-api.com/api/timeseries?access_key=nc4rq9ho37s9r4m7r7bd4jaw46b940amnq40h71wcj54cbfweprfm113awf4&start_date=${startDate}&end_date=${endDate}&symbols=COFFEE,ROBUSTA`);
        const data = await response.json();

        if (data && data.rates) {
            const transformedData = Object.entries(data.rates).map(([date, rates]) => ({
                date,
                COFFEE: 1 / rates.COFFEE,
                ROBUSTA: 1 / rates.ROBUSTA
            }));
            setChartData(transformedData);
        }
    };

    fetchTimeSeries();
}, []);


