import React, { useEffect, useState } from 'react';

const CommoditiesPrices = () => {
    const [prices, setPrices] = useState({ COFFEE: null, ROBUSTA: null });
    const [units, setUnits] = useState({ COFFEE: null, ROBUSTA: null }); // New state for units

    const divStyle = {
        padding: '20px',
        margin: '10px',
        width: '200px',
        background: 'rgba(0,255,133,0.35)',
        boxShadow: '20px 20px 40px -6px rgba(0,0,0,0.2)',
        backdropFilter: 'blur(10.5px)',
        WebkitBackdropFilter: 'blur(10.5px',
        borderRadius: '10px',
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        justifyContent: 'center'
    };

    useEffect(() => {
        const fetchData = async () => {
            try {
                const response = await fetch(`https://commodities-api.com/api/latest?access_key=nc4rq9ho37s9r4m7r7bd4jaw46b940amnq40h71wcj54cbfweprfm113awf4&base=USD&symbols=COFFEE,ROBUSTA`);
                const { data } = await response.json();

                console.log("API Response:", data);

                if (data && data.rates) {
                    const coffeePrice = data.rates.COFFEE;
                    const robustaPrice = data.rates.ROBUSTA;
                    setPrices({ COFFEE: coffeePrice, ROBUSTA: robustaPrice });

                    // Setting units
                    if (data.unit) {
                        setUnits({ COFFEE: data.unit.COFFEE, ROBUSTA: data.unit.ROBUSTA });
                    }
                }

            } catch (error) {
                console.error("Error fetching commodities data:", error);
            }
        };

        fetchData();
    }, []);

    return (
        <div style={{ display: 'flex', justifyContent: 'center' }}>
            <div style={divStyle}>
                Coffee (COFFEE): 
                <br />
                {prices.COFFEE && (1 / prices.COFFEE).toFixed(2)} {units.COFFEE}
            </div>
            <div style={divStyle}>
                Robusta (ROBUSTA): 
                <br />
                {prices.ROBUSTA && (1 / prices.ROBUSTA).toFixed(2)} {units.ROBUSTA}
            </div>
        </div>
    );
};

export default CommoditiesPrices;
