import React from 'react';
import Data from '../../Data';
import {
    Container,
    HeroContainer,
    BodyContainer
} from '../StyledComponents/StyledComponents';
import Button from '../Utility/Button';

const HomeHero = () => {
    return(
        <HeroContainer
            animate={{ opacity: 1 }}
            initial={{ opacity: 0 }}
            transition={{ duration: 2, delay: 1 }}
            exit={{ opacity: 0 }}
        >

        <BodyContainer style={{
        marginTop: 'calc(200px - 1vw)'
        }}>
                <Container width={80} leftAlign bottom={2} >
                    <h1>{Data.home.title}</h1>
                </Container>

                <Container  
                leftAlign 
                bottom={6} 
                width={65}
                style={{
                    backgroundImage: 'url("/banner2.svg")',
                    backgroundPosition: 'center',
                    backgroundSize: 'cover',  // Adjust as per your requirements
                    backgroundRepeat: 'no-repeat',
                    minHeight: '200px',  // Adjust based on the size of the image or content
                }}
            >
                    <p>{Data.home.description}</p>
                </Container>
                <Container style={{ alignSelf: 'flex-end' }} bottom={-10} width={65}>
                 <img src="/Glocafe.svg" alt="Logo" style={{ maxHeight: '20vw', marginLeft: '20vw' }} />
                </Container>

                <a href="/about">
                    <Button
                        route="/about"
                        text="About Us"
                        right
                    />
                </a>
            </BodyContainer>
        </HeroContainer>
    )
}

export default HomeHero;
