import React, { useState, useEffect } from 'react';
import './home.css';

const API_ENDPOINT = "https://newsdata.io/api/1/news";
const API_KEY = "pub_2921643ce8e5a188a8aa436bfb4fa28e7d827";

function NewsFeed() {
    const [articles, setArticles] = useState([]);

    useEffect(() => {
        const query = `(coffee OR ARABICA OR ROBUSTA) AND (economic OR business) NOT (recipe OR shop)`;
        fetch(`${API_ENDPOINT}?apikey=${API_KEY}&q=${encodeURIComponent(query)}&size=10&language=en`)
            .then(response => response.json())
            .then(data => {
                if (data.status === "ok" && data.articles) {
                    setArticles(data.articles);
                }
            });
    }, []);

    return (
        <div className="news-feed">
            {articles.map(article => (
                <div className="article" key={article.uuid}>
                    <img src={article.image_url} alt={article.title} />
                    <h2>{article.title}</h2>
                    <p>{article.description}</p>
                    <a href={article.url} target="_blank" rel="noopener noreferrer">Read more</a>
                </div>
            ))}
        </div>
    );
}

export default NewsFeed;
