import React, { useEffect, useState } from 'react';
import { LineChart, Line, ResponsiveContainer } from 'recharts';

const CommoditiesPrices = () => {
    const [prices, setPrices] = useState({ COFFEE: null, ROBUSTA: null });
    const [units, setUnits] = useState({ COFFEE: null, ROBUSTA: null });
    const [coffeeChartData, setCoffeeChartData] = useState([]);
    const [robustaChartData, setRobustaChartData] = useState([]);
    const [loading, setLoading] = useState(true);

    const divStyle = {
        padding: '20px',
        margin: '10px',
        width: '200px',
        background: 'rgba(0,255,133,0.35)',
        boxShadow: '20px 20px 40px -6px rgba(0,0,0,0.2)',
        backdropFilter: 'blur(10.5px)',
        WebkitBackdropFilter: 'blur(10.5px)',
        borderRadius: '10px',
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        justifyContent: 'center'
    };

    useEffect(() => {
        const fetchData = async () => {
            try {
                const response = await fetch(`https://commodities-api.com/api/latest?access_key=YOUR_API_KEY&base=USD&symbols=COFFEE,ROBUSTA`);
                const { data } = await response.json();
                console.log("Latest Prices Response:", data);
                if (data && data.rates) {
                    setPrices({ COFFEE: data.rates.COFFEE, ROBUSTA: data.rates.ROBUSTA });
                    if (data.unit) {
                        setUnits({ COFFEE: data.unit.COFFEE, ROBUSTA: data.unit.ROBUSTA });
                    }
                }
            } catch (error) {
                console.error("Error fetching commodities data:", error);
            }
        };

        const fetchTimeSeries = async (symbol) => {
            const currentDate = new Date();
            const endDate = new Date(currentDate.setDate(currentDate.getDate() - 1)).toISOString().split('T')[0];
            const startDate = new Date(currentDate.setDate(currentDate.getDate() - 20)).toISOString().split('T')[0];
            const response = await fetch(`https://commodities-api.com/api/timeseries?access_key=YOUR_API_KEY&start_date=${startDate}&end_date=${endDate}&symbols=${symbol}`);
            const data = await response.json();
            console.log(`Raw API Response for ${symbol}:`, data);

            if (data && data.rates) {
                const transformedData = Object.entries(data.rates).map(([date, rates]) => ({
                    date,
                    rate: rates[symbol] ? 1 / rates[symbol] : null
                })).filter(item => item.rate !== null);

                if (symbol === 'COFFEE') {
                    setCoffeeChartData(transformedData);
                } else {
                    setRobustaChartData(transformedData);
                }
            }
        };

        (async () => {
            await fetchData();
            await fetchTimeSeries('COFFEE');
            await fetchTimeSeries('ROBUSTA');
            console.log("Chart Data State (Coffee): ", coffeeChartData);
            console.log("Chart Data State (Robusta): ", robustaChartData);
            setLoading(false);
        })();
    }, []);

    if (loading) {
        return <div>Loading...</div>;
    }

    return (
        <div style={{ display: 'flex', justifyContent: 'center' }}>
            <div style={divStyle}>
                Latest Price ARABICA: 
                <br />
                {prices.COFFEE && (1 / prices.COFFEE).toFixed(2)} {units.COFFEE}
                <ResponsiveContainer width="100%" height={80}>
                    <LineChart width={300} height={80} data={coffeeChartData}>
                        <Line type="monotone" dataKey="rate" stroke="#C981AF" strokeWidth={2} />
                    </LineChart>
                </ResponsiveContainer>
            </div>
            <div style={divStyle}>
                Latest Price ROBUSTA: 
                <br />
                {prices.ROBUSTA && (1 / prices.ROBUSTA).toFixed(2)} {units.ROBUSTA}
                <ResponsiveContainer width="100%" height={80}>
                    <LineChart width={300} height={80} data={robustaChartData}>
                        <Line type="monotone" dataKey="rate" stroke="#C981AF" strokeWidth={2} />
                    </LineChart>
                </ResponsiveContainer>
            </div>
        </div>
    );
};

export default CommoditiesPrices;
