import React from 'react';
import Data from '../../Data';
import {
    Container,
    HeroContainer,
    BodyContainer
} from '../StyledComponents/StyledComponents';
import Button from '../Utility/Button';

const HomeHero = () => {
    return(
        <HeroContainer
            animate={{ opacity: 1 }}
            initial={{ opacity: 0 }}
            transition={{ duration: 2, delay: 1 }}
            exit={{ opacity: 0 }}
        >
            <video autoPlay loop muted playbackRate={0.1} style={{ position: "absolute", width: "100%", height: "100%", objectFit: "cover", zIndex: -1 }}>
                <source src="/pexels-michael-burrows-7116416 (2160p).mp4" type="video/mp4" />
                Your browser does not support the video tag.
            </video>
            <div style={{ position: "absolute", top: 0, left: 0, right: 0, bottom: 0, backgroundColor: "rgba(0, 0, 0, 0.5)", zIndex: 0 }}></div>

            <BodyContainer>
                <Container width={80} leftAlign bottom={2} top={20}>
                    <h1>{Data.home.title}</h1>
                </Container>

                <Container leftAlign bottom={6} width={65}>
                    <p>{Data.home.description}</p>
                </Container>
                <Container style={{ alignSelf: 'flex-end' }} bottom={-10} width={65}>
                 <img src="/Glocafe.svg" alt="Logo" style={{ maxHeight: '20vw', marginLeft: '20vw' }} />
                </Container>

                <a href="/about">
                    <Button
                        route="/about"
                        text="About Us"
                        right
                    />
                </a>
            </BodyContainer>
        </HeroContainer>
    )
}

export default HomeHero;
