import React, { useEffect, useState } from 'react';

const CommoditiesPrices = () => {
    const [prices, setPrices] = useState({ COFFEE: null, ROBUSTA: null });

    useEffect(() => {
        const fetchData = async () => {
            try {
                const response = await fetch(`https://commodities-api.com/api/latest?access_key=nc4rq9ho37s9r4m7r7bd4jaw46b940amnq40h71wcj54cbfweprfm113awf4&base=USD&symbols=COFFEE,ROBUSTA`);
                const data = await response.json();

                console.log("API Response:", data);  // Log the API response

                // Check if data.rates exists
                if (data && data.rates) {
                    const coffeePrice = data.rates.COFFEE;
                    const robustaPrice = data.rates.ROBUSTA;

                    setPrices({ COFFEE: coffeePrice, ROBUSTA: robustaPrice });
                } else {
                    console.warn("Unexpected API response structure");
                }

            } catch (error) {
                console.error("Error fetching commodities data:", error);
            }
        };

        fetchData();
    }, []);

    return (
        <div style={{ display: 'flex', justifyContent: 'space-between' }}>
            <div style={{ padding: '10px', border: '1px solid black' }}>
                Coffee (COFFEE): ${prices.COFFEE && prices.COFFEE.toFixed(2)} per lb
            </div>
            <div style={{ padding: '10px', border: '1px solid black' }}>
                Robusta (ROBUSTA): ${prices.ROBUSTA && prices.ROBUSTA.toFixed(2)} per lb
            </div>
        </div>
    );
};

export default CommoditiesPrices;
