import React from 'react';
import Data from '../../Data';
import { motion } from 'framer-motion';
import {
    Container,
    HeroContainer,
    BodyContainer
} from '../StyledComponents/StyledComponents';
import Button from '../Utility/Button';

const HomeHero = () => {
    return (
        <HeroContainer
            animate={{ opacity: 1 }}
            initial={{ opacity: 0 }}
            transition={{ duration: 2, delay: 1 }}
            exit={{ opacity: 0 }}
        >
            <BodyContainer style={{
                width: '80%',
                height: '70vh',
                marginTop: '2vh',
                backgroundColor: '#d5bdaf',
                borderRadius: '20px',
                boxShadow: '0px 4px 8px rgba(0, 0, 0, 0.1)',
                position: 'relative',
            }}>
                <Container width={80} leftAlign bottom={3} top={12}>
                    <h1>{Data.home.title}</h1>
                </Container>

                <Container
                    leftAlign
                    bottom={2}
                    width={65}
                    style={{
                        backgroundImage: 'url("/banner2.svg")',
                        backgroundPosition: 'bottom',
                        backgroundRepeat: 'no-repeat',
                        backgroundSize: 'contain',
                        minHeight: '200px',
                    }}
                >
                    <p>{Data.home.description}</p>
                </Container>
                <Container style={{ alignSelf: 'flex-end' }} width={65}>
                    <motion.img src="/Glocafe.svg" alt="Logo" style={{
                        maxWidth: '100%',
                        minWidth: '260px',
                        maxHeight: '20vw',
                        position: 'absolute',
                        top: '-7vw',
                        right: '-10vw',
                        '@media (max-width: 1024px)': { // Adjust this breakpoint as needed
                            right: '50%',
                            transform: 'translateX(50%)' // Center the logo horizontally for smaller screens
                        }
                    }}
                        animate={{ opacity: 1 }}
                        initial={{ opacity: 0 }}
                        transition={{ duration: 1, delay: 2 }}
                        exit={{ opacity: 0 }} />
                </Container>

                <a href="/about">
                    <Button
                        route="/about"
                        text="About Us"
                        right
                    />
                </a>
            </BodyContainer>
        </HeroContainer>
    )
}

export default HomeHero;
